// 大货拣货

import React from 'react'

// 引入输入框组件     使用ant design mobile的ui库
import { InputItem } from 'antd-mobile';

// 引入标题导航组件
import Navs from '../../../componets/nav'
// 引入input组件
// import Inp from '../../../componets/input'
// 引入样式文件
import IndexCss from './css/pickup.module.scss'

// 引入复选框组件
import { List, Checkbox, Flex } from 'antd-mobile';

class Pickup extends React.Component {
    state = {
        boxAdd: "",
        closeAdd: "",
        weight: "",
        lenghtCM: "",
        widthCM: "",
        tallCM: "",
        checkList: [
            {
                name: "更新模式",
                check: true
            }, {
                name: "不使用清关号",
                check: true
            }, {
                name: "锁定重量",
                check: true
            }, {
                name: "锁定尺寸",
                check: true
            }, {
                name: "使用客户数据",
                check: true
            }
        ]
    }
    // -------------------------------------------------------------------

    // 点击在输入框在运单号的时候触发此函数
    hendleChangeGoaut = (v) => {
        // console.log(v.keyCode);
        // 里面做一层判断  确认用户点击回车键后进此判断
        if (v.keyCode === 13) {
            // 里面判断用户是否使用清单号模式   如果使用了清单号模式进入if判断
            if(this.refs.rvomadd){
                // 利用ref查找到清单号的输入框
                let rvomadd = this.refs.rvomadd
                // 为其获取焦点
                rvomadd.focus()
            }
            // 如果以上的if条件没有满足证明用户没有使用到清单模式
            // 则需要直接跳到重量模式
            let weight = this.refs.weight
            // 并为其重量模式添加focus
            weight.focus()
        }

    }

    // 下面是清单输入框的键盘点击事件
    handleRvomAdd = (v) => {
        // 里面做出一层判断是否点击了回车键
        if (v.keyCode === 13) {
            // 如果点击了回车键则进入if判断
            let weight = this.refs.weight
            // 并未下一个输入框获取焦点
            weight.focus()
        }
    }

    // 下面是重量输入框的键盘事件函数
    handleKeyWeight = (v) => {

        // 判断键盘事件是否点击的是回车键    如果是就进入if判断
        if (v.keyCode === 13) {
            // 通过ref 找点下一个input输入框   
            let length = this.refs.length
            // 添加focus事件     自动获取焦点
            length.focus()
        }
    }

    // 下面是长输入框的键盘事件函数
    keyDownLength = (v) => {
        // 判断键盘事件是否点击的是回车键    如果是就进入if判断
        if (v.keyCode === 13) {
            // 通过ref 找点下一个input输入框
            let width = this.refs.width
            // 添加focus事件     自动获取焦点
            width.focus()
        }
    }

    // 下面是宽输入框的键盘事件函数
    keyDownWidth = (v) => {
        // 判断键盘事件是否点击的是回车键    如果是就进入if判断
        if (v.keyCode === 13) {
            // 通过ref 找点下一个input输入框
            let tall = this.refs.tall
            // 添加focus事件     自动获取焦点
            tall.focus()
        }
    }

    keyDownTall = () => {

    }


    // 点击handleCheckList触发的函数
    handleCheckList = (i) => {
        // render函数上面循环了六个check选择模式     所以采取 i 来找到相应的对象

        // 创建一个变量拿到checkList数组   并未其进行改造
        let checkList = this.state.checkList
        // 通过索引来取到他的值    并为check取反
        checkList[i].check = !checkList[i].check
        this.setState({
            checkList
        })


    }
    render() {

        return (
            <div>
                <Navs name="拣货" />
                <div className={IndexCss.inp}>
                    <div className={IndexCss.boxAdd}>
                        <span>运单号/箱号</span><div><InputItem autoFocus="autofocus" value={this.state.boxAdd} onChange={(v) => {
                            this.setState({
                                boxAdd: v
                            })
                        }} onKeyDown={this.hendleChangeGoaut} /></div>
                    </div>
                    {this.state.checkList[0].check &&
                        <div className={IndexCss.update}>
                            {this.state.checkList[1].check && <div><span>清关号</span><div><InputItem value={this.state.closeAdd} onChange={(v) => {
                                this.setState({
                                    closeAdd: v
                                })
                            }}
                                onKeyDown={this.handleRvomAdd}
                                ref="rvomadd"
                            />
                            </div> </div>}
                            <div>
                                <span>重量</span><div>
                                    <InputItem onChange={(v) => {
                                        this.setState({
                                            weight: v
                                        })
                                    }}
                                        onKeyDown={this.handleKeyWeight}
                                        ref="weight"
                                    />
                                </div>
                            </div>
                        </div>
                    }

                    {this.state.checkList[0].check && <div className={IndexCss.Divthree}>
                        <div className={IndexCss.length}><InputItem placeholder="长" ref="length" onChange={(v) => {
                            this.setState({
                                lenghtCM: v
                            })
                        }}
                            onKeyDown={this.keyDownLength}
                        /></div>
                        <div className={IndexCss.length}><InputItem placeholder="宽" ref="width" onChange={(v) => {
                            this.setState({
                                widthCM: v
                            })
                        }}
                            onKeyDown={this.keyDownWidth}
                        /></div>
                        <div className={IndexCss.length}><InputItem placeholder="高" ref="tall" onChange={(v) => {
                            this.setState({
                                tallCM: v
                            })
                        }}
                            onKeyDown={this.keyDownTall}
                        /></div>
                    </div>}
                    {this.state.checkList.map((v, i) => {
                        return <div key={i} className={IndexCss.checkMap}>
                            <Checkbox onChange={() => ((i) => {
                                this.handleCheckList(i)
                            })(i)}>
                                {v.name}
                            </Checkbox>
                        </div>
                    })}
                </div>

            </div>
        );
    }
}

export default Pickup;
