// 封装一个Promise对象的axios请求

// 引入axios 模块
import axios from 'axios'


// 创建一个函数  并暴露出去
export const Post = (data)=>{
    return new Promise((resolve, reject)=>{
        axios.post(data).then(res=>{
            resolve(res)
        })
    })
}
