// 大货拣货

import React from 'react'

// 引入输入框组件     使用ant design mobile的ui库
import { InputItem } from 'antd-mobile';

// 引入标题导航组件
import Navs from '../../../componets/nav'
// 引入input组件
// import Inp from '../../../componets/input'
// 引入样式文件
import IndexCss from './css/pickup.module.scss'
class Pickup extends React.Component {
    state = {
        boxAdd: "",
        closeAdd: "",
        weight:"",
        lenghtCM:"",
        widthCM:"",
        tallCM:""
    }
    handleKeyDown = () => {

    }
    hendleChangeText = (v)=>{
        this.setState({
            boxAdd: v
        })
    }

    hendleChangeClose = (v) => {
        this.setState({
            closeAdd: v
        })
    }
    hendleChangeWeight = (v) => {
        this.setState({
            weight:v
        })
    }





    
    render() {

        return (
            <div>
                <Navs name="拣货" />
                <div className={IndexCss.inp}>
                    <ul>
                        <li>
                            <span>运单号/箱号</span><div><InputItem autoFocus="autofocus" value={this.state.boxAdd} onChange={this.hendleChangeText} onKeyDown={this.handleKeyOne} /></div>
                        </li>
                        <li>
                            <span>清关号</span><div><InputItem value={this.state.closeAdd} onChange={this.hendleChangeClose}
                             onKeyDown={this.handleKeyTwo}
                             ref="rvomadd"
                             />
                            </div>
                        </li>
                        <li><span>重量</span><div>
                            <InputItem onChange={this.hendleChangeWeight}
                             onKeyDown={this.handleKeyDown}
                             ref="weight"
                             />
                        </div></li>
                    </ul>

                    <div className={IndexCss.Divthree}>
                        <div className={IndexCss.length}><InputItem placeholder="长" ref="length" /></div>
                        <div className={IndexCss.length}><InputItem placeholder="宽" ref="width" /></div>
                        <div className={IndexCss.length}><InputItem placeholder="高" ref="tall" /></div>
                    </div>
                </div>

            </div>
        );
    }
}

export default Pickup;
