// 这是一个录用跟踪号的组件页面

// 引入react
import React from 'react'

import { List, InputItem } from 'antd-mobile';

// 引入axios请求 
import Post from '../../../axios/request'
// 引入导航模块
import Navs from '../../../componets/nav'

import { Toast } from 'antd-mobile';

import IndexCss from './css/enteringtracking.module.scss'
// 创建类组件
class Entering extends React.Component {

    state = {
        select: false,
        req_id: "",
        number: "",
        Data: {
            dataList: [],
            tailAfter: [],
            boxList: []
        },
        inpSlt: 1,
        userData: 0,
        userNuber: 0
    }

    componentDidMount() {
    }
    // 获取到用户填写的单号
    handelCase = (v) => {
        this.setState({
            req_id: v
        })
    }


    // 获取到用户填写的跟踪号
    handelTail = (v) => {
        this.setState({
            number: v
        })
    }

    // 当用户按下单号的回车键触发此函数
    handelGoNext = (e) => {
        if (e.keyCode === 13) {
            this.setState({
                inpSlt: 0
            })
        }
    }
    // 这是发送axios请求的函数
    handelKeyup = async (event) => {

        // 当用户按下回车键触发此函数
        let { req_id, number, userData } = this.state
        let Data = this.state.Data
        // console.log(Data.boxList);
        let { dataList, boxList, tailAfter } = Data
        // 判断是否按下的是回车键    如果是则进入此判断
        if (event.keyCode === 13 && req_id && number !== '') {
            // 添加一个遮罩
            Toast.loading('录入中...')
            // 只要有axios请求发送就添加扫描数
            userData++
            // 发送axios请求
            let res = await Post("/api/test/enteringtracking?sample=1", { "lading_number": "UPS", req_id, number })
            res.num = req_id
            let tail = tailAfter.indexOf(number)
            if (tail !== -1) {
                res.text = "跟踪号重复"
                dataList[tail].text = "跟踪号重复"
            } else if (tail === -1) {
                tailAfter.unshift(number)
            }
            // res.num = req_id
            dataList.unshift(res)
            console.log(dataList);

            // ------------------------------------------------------------------------------------------

            let box = boxList.indexOf(req_id)
            if (box === -1) {
                boxList.unshift(req_id)
            }
            // 如果请求回来则把遮罩关闭
            Toast.hide()
            Data = { boxList, dataList, tailAfter }
            this.setState({
                Data,
                req_id: '',
                number: '',
                userData
            })
        }else if (event.keyCode === 16) {
            // 添加一个遮罩
            Toast.loading('录入中...')
            userData++
            let res = await Post("/api/test/lading_scan_check?sample=0", { "lading_number": "UPS", req_id, number })
            res.num = req_id
            dataList.unshift(res)
            // 如果请求回来则把遮罩关闭
            Toast.hide()
            let box = boxList.indexOf(req_id)
            if (box === -1) {
                boxList.unshift(req_id)
            }
            this.setState({
                Data,
                req_id: '',
                number: '',
                userData
            })
        }
    }
    // 定义一个变量来判断是否需要return 那一个标签
    divSve = (data) => {
        if (data === 0) {
            return <InputItem
                autoFocus="autofocus"
                clear
                onChange={this.handelTail}
                onKeyDown={this.handelKeyup}
                onBlur={() => { this.setState({ inpSlt: 1 }) }}
                value={this.state.number}
            >跟踪号</InputItem>
        } else {
            return <div className={IndexCss.tailHas}>
                <div className={IndexCss.tailHasTitle}>跟踪号</div>
                <div className={IndexCss.tailHasText} onClick={this.handelClickNtxt}>{this.state.number}</div>
            </div>
        }
    }

    handelClickNtxt = () => {
        // console.log(1);

        this.setState({
            inpSlt: 0
        })
    }

    RovmClick = ()=>{
        let { dataList, boxList, tailAfter } = this.state.Data
        dataList = []
        boxList = []
        tailAfter = []
