// 这是一个托盘管理页面
// 引入react
import React from 'react'
// 引入标签页组件
import { Tabs, WhiteSpace, Badge } from 'antd-mobile';

const tabs = [
    { title: <Badge text={'3'}>First Tab</Badge> },
    { title: <Badge text={'今日(20)'}>Second Tab</Badge> },
    { title: <Badge dot>Third Tab</Badge> },
];


// 创建一个类组件
class Pallet extends React.Component {
    render() {
        return <div>
            <Tabs tabs={tabs}
                initialPage={1}
                onChange={(tab, index) => { console.log('onChange', index, tab); }}
                onTabClick={(tab, index) => { console.log('onTabClick', index, tab); }}
            >
                <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: calc( 100 - 45), backgroundColor: '#fff' }}>
                    Content of first tab
          </div>
                <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '100vh', backgroundColor: '#fff' }}>
                    Content of second tab
          </div>
                <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '100vh', backgroundColor: '#fff' }}>
                    Content of third tab
          </div>
            </Tabs>
        </div>
    }
}


export default Pallet
