// 大货拣货

import React from 'react'

// 引入输入框组件     使用ant design mobile的ui库
import { InputItem } from 'antd-mobile';

// 引入标题导航组件
import Navs from '../../../componets/nav'
// 引入input组件
// import Inp from '../../../componets/input'
// 引入样式文件
import IndexCss from './css/pickup.module.scss'
class Pickup extends React.Component {
    state = {
        one: false,
        two: "",
        oneText: "",
        twoText: ""
    }
    handleKeyDown = () => {

    }

    hendleChangeTwo = (v) => {
        this.setState({
            oneText: v
        })
    }
    hendleChangeThree = (v) => {
        this.setState({
            twoText: v
        })
    }
    render() {
        // console.log(this.one === true?'1':'0');

        return (
            <div>
                <Navs name="拣货" />
                <div className={IndexCss.inp}>
                    <ul>
                        <li>
                            <span>运单号/箱号</span><div><InputItem onChange={this.hendleChangeOne} onKeyDown={this.handleKeyOne} /></div>
                        </li>
                        <li>
                            <span>清关号</span><div>{this.state.one === true ? <InputItem autoFocus="autofocus" value={this.state.oneText} onChange={this.hendleChangeTwo}
                            onBlur={() => { this.setState({ one: false }) }} onKeyDown={this.handleKeyTwo} />
                            : <div className={IndexCss.divBlur} onClick={() => { this.setState({ one: true }) }}>{this.state.oneText}</div>}</div>
                        </li>
                        <li><span>重量</span><div>{this.state.two === true ?
                            <InputItem autoFocus="autofocus" value={this.state.twoText} onChange={this.hendleChangeThree}
                                onBlur={() => { this.setState({ two: false }) }} onKeyDown={this.handleKeyDown} />
                            : <div className={IndexCss.divBlur} onClick={() => { this.setState({ two: true }) }}>{this.state.twoText}</div>}
                        </div></li>
                    </ul>

                    <div className={IndexCss.Divthree}>
                        <div className={IndexCss.length}><InputItem placeholder="长" /></div>
                        <div className={IndexCss.length}><InputItem placeholder="宽" /></div>
                        <div className={IndexCss.length}><InputItem placeholder="高" /></div>
                    </div>
                </div>

            </div>
        );
    }
}

export default Pickup;
