// 这是一个录用跟踪号的组件页面

// 引入react
import React from 'react'

import { List, InputItem } from 'antd-mobile';

// 引入axios请求 
import Post from '../../../axios/request'
// 引入导航模块
import Navs from '../../../componets/nav'

import { Toast } from 'antd-mobile';

import IndexCss from './css/enteringtracking.module.scss'
// 创建类组件
class Entering extends React.Component {

    state = {
        select: false,
        req_id: "",
        number: "",
        dataList: [],
        inpSlt: 1,
        userData: 0,
        userNuber: []
    }

    componentDidMount() {
    }
    // 获取到用户填写的单号
    handelCase = (v) => {
        this.setState({
            req_id: v
        })
    }


    // 获取到用户填写的跟踪号
    handelTail = (v) => {
        this.setState({
            number: v
        })
    }

    // 当用户按下单号的回车键触发此函数
    handelGoNext = (e) => {
        if (e.keyCode === 13) {
            this.setState({
                inpSlt: 0
            })
        }
    }
    // 这是发送axios请求的函数
    handelKeyup = async (event) => {

        // 当用户按下回车键触发此函数
        let { req_id, number, dataList, userData } = this.state
        // 判断是否按下的是回车键    如果是则进入此判断
        if (event.keyCode === 13 && req_id && number !== '') {
            Toast.loading('录入中...')
            userData++
            let res = await Post("/api/test/enteringtracking?sample=1", { "lading_number": "UPS", req_id, number })
            dataList.unshift(res)
            Toast.hide()
            this.setState({
                dataList,
                req_id: '',
                number: '',
                userData
            })
        } else if (event.keyCode === 16) {
            userData++
            let res = Post("/api/test/lading_scan_check?sample=0", { "lading_number": "UPS", req_id, number })
            console.log(res);
        }
    }
    // 定义一个变量来判断是否需要return 那一个标签
    divSve = (data) => {
        if (data === 0) {
            return <InputItem
                autoFocus="autofocus"
                clear
                onChange={this.handelTail}
                onKeyDown={this.handelKeyup}
                onBlur={() => { this.setState({ inpSlt: 1 }) }}
                value={this.state.number}
            >跟踪号</InputItem>
        } else {
            return <div className={IndexCss.tailHas}>
                <div className={IndexCss.tailHasTitle}>跟踪号</div>
                <div className={IndexCss.tailHasText} onClick={this.handelClickNtxt}>{this.state.number}</div>
            </div>
        }
    }

    handelClickNtxt = () => {
        console.log(1);

        this.setState({
            inpSlt: 0
        })
    }
    render() {
        return <div>
            <div className={IndexCss.nav}> <Navs name="录入跟踪号" /></div>
            <div className={IndexCss.bodys}>
                <div className={IndexCss.selectAdd}>
                    <span className={IndexCss.selectTitle}>快递服务</span>
                    <select className={IndexCss.commText}>
                        <option value="顺丰快递">顺丰快递</option>
                        <option value="中通快递">中通快递</option>
                        <option value="申通快递">申通快递</option>
                        <option value="天天快递">天天快递</option>
                        <option value="顺丰快递">顺丰快递</option>
                        <option value="中通快递">中通快递</option>
                        <option value="申通快递">申通快递</option>
                        <option value="天天快递">天天快递</option>
                        <option value="顺丰快递">顺丰快递</option>
                        <option value="中通快递">中通快递</option>
                        <option value="申通快递">申通快递</option>
                        <option value="天天快递">天天快递</option>
                        <option value="顺丰快递">顺丰快递</option>
                        <option value="中通快递">中通快递</option>
                        <option value="申通快递">申通快递</option>
                        <option value="天天快递">天天快递</option>
                        <option value="顺丰快递">顺丰快递</option>
                        <option value="中通快递">中通快递</option>
                        <option value="申通快递">申通快递</option>
                        <option value="天天快递">天天快递</option>
                        <option value="顺丰快递">顺丰快递</option>
                        <option value="中通快递">中通快递</option>
                        <option value="申通快递">申通快递</option>
                        <option value="天天快递">天天快递</option>
                        <option value="顺丰快递">顺丰快递</option>
                        <option value="中通快递">中通快递</option>
                        <option value="申通快递">申通快递</option>
                        <option value="天天快递">天天快递</option>
                        <option value="顺丰快递">顺丰快递</option>
                        <option value="中通快递">中通快递</option>
                        <option value="申通快递">申通快递</option>
                        <option value="天天快递">天天快递</option>
                    </select>
                </div>
                <List>
                    <InputItem
                        clear
                        onChange={this.handelCase}
                        onKeyDown={this.handelGoNext}
                        value={this.state.req_id}
                    >箱号</InputItem>
                    {this.divSve(this.state.inpSlt)}
                </List>
            </div>
            <div className={IndexCss.numberUser}><span>录入数次{this.state.userData}</span> <span>箱子个数{this.state.userNuber.length}</span></div>
            {this.state.dataList && <div>
                {
                    this.state.dataList.map((v, i) => {
                        // console.log(v);
                        if (v.info) {
                            return <div key={i} className={IndexCss.text}><div>{v.req_id}</div>
                                <div>{v.num}</div>
                                <div>{v.info}</div>
                            </div>
                        } else {
                            return <div key={i} className={IndexCss.cont}>
                                <div>{v.req_id}</div>
                                <div>{v.num}</div>
                            </div>
                        }
                    })
                }
            </div>}
        </div>
    }
}

export default Entering
