// 这是一个查货核对的订单

// 引入react 
import React from 'react'
// 引入navs组件
import Navs from '../../../componets/nav'

// 引入ui库的input标签
import { InputItem } from 'antd-mobile';

// 引入 局部样式 
import IndexCss from './css/chahuo.module.scss'
// 引入DuvINp 组件
import InpDiv from '../../../componets/inp-shade'
class Chahuo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            OddNuberOne:'',
            OddNuberTwo:''
        }
    }
    componentDidMount() {
        let inputEl = this.refs.input;
        inputEl.focus()
    }


    hendelGoNext = (v) => {
        if (v.keyCode === 13) {
            let inputText = this.refs.inputText
            inputText.focus()
        }
    }
    render() {
        return (
            <div>
                <Navs name="查货核对" />
                <div className={IndexCss.chahuo}>
                    <div className={IndexCss.chuhuodan}>
                        <div className={IndexCss.chuhuoTetx}>
                            出货单号
                            </div>
                        <InputItem
                            ref="input"
                            onKeyDown={this.hendelGoNext}
                            onChange={(v)=>{this.setState({OddNuberOne:v})}}
                        />
                    </div>
                    <div className={IndexCss.chuhuodan}>
                        <div className={IndexCss.chuhuoTetx}>
                            出货箱
                            </div>
                        <InputItem
                            ref="inputText"
                            onChange={(v)=>{this.setState({OddNuberTwo:v})}}
                        />
                    </div>
                </div>
            </div>
        );
    }
}

export default Chahuo;
